import React, { Component } from "react";
import "../css/ToDoList.css";

class ToDoList extends Component<Props> {
  constructor() {
    super();
    this.state = {
      list_items: [],
      input_val: ""
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = event => {
    this.setState({ input_val: event.target.value });
  };

  populateList = cur_item => {
    let new_arr = this.state.list_items.slice();
    new_arr.push(
      <div>
        <text>{cur_item}</text>
      </div>
    );
    this.setState({
      list_items: new_arr
    });
  };

  render() {
    return (
      <div className="to-do-list-container">
        <input
          type="text"
          value={this.state.input_val}
          onChange={this.handleChange}
        />
        <button onClick={this.populateList(this.state.input_val)}>
          submit
        </button>
        <div>{this.state.list_items}</div>
      </div>
    );
  }
}

export default ToDoList;
