import React, { Component } from "react";
import ToDoHeader from "./ToDoHeader.js";
import ToDo from "./ToDo.js";
import "../css/ToDoList.css";

class ToDoListContainer extends Component<Props> {
  state = {
    list_items: [],
    input_val: ""
  };

  addTodo = new_item => {
    const new_list = [];
    new_list.push(new_item);
    this.setState({ list_items: new_list });
  };

  populateItems = () => {
    all_todos = [];
    for (let i = 0; i < this.state.list_items.length; i++) {
      all_todos.push(<ToDo item={this.state.list_items[i]} />);
    }
  };

  render() {
    return (
      <div className="to-do-list-container">
        <ToDoHeader onAdd={this.addTodo} />
        <div>{this.state.list_items}</div>
      </div>
    );
  }
}

export default ToDoListContainer;
